sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
    // Create GlideDateTime objects for the current date and the passed in date
    var rightnow = new GlideDateTime();
    var testdate = new GlideDateTime(strDate);
	var currDay=rightnow.getDayOfMonthLocalTime();
    var testdatedDay=testdate.getDayOfMonthLocalTime();

    // If the testdate is before rightnow, return true, else return false
    if(testdate.before(rightnow) || currDay==testdatedDay){
    // gs.addErrorMessage("When needed date should be in the future date.  Your request has not been saved to the database.");
    // current.setAbortAction(true);
	return true;
  }
  else{
	return false;
  }
  },

    type: 'NeedItUtils'
};
